    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // TODO add your handling code here:
        String userid=jTextField1.getText();
    String username=jTextField2.getText();
    if(userid.equals("") && username.equals("") ){
           JOptionPane.showMessageDialog(this , "Please fill ID and Password");
           jTextField1.setText("");
           jTextField2.setText("");
          
           
        }else if(username.equals("") ){
           JOptionPane.showMessageDialog(this , "Please enter password");
           jTextField1.setText("");
           jTextField2.setText("");
           
        }else if(userid.equals("")){
           JOptionPane.showMessageDialog(this , "Please fill ID");
           jTextField1.setText("");
           jTextField2.setText("");
           
        } else{
        
            
        try{    
            ResultSet rs;
            Class.forName("org.apache.derby.jdbc.ClientDriver");
            Connection con=(Connection) DriverManager.getConnection("jdbc:derby://localhost:1527/DB", "root", "root");
            try (PreparedStatement ps = (PreparedStatement) con.prepareCall(" SELECT * FROM register where id=? and name=?")) {
                ps.setString(1, userid);
                ps.setString(2, username);
                rs=ps.executeQuery();
                while(rs.next()){
                    JOptionPane.showMessageDialog(this, "You are logged in!!");
                    Status s=new Status();
                    s.setVisible(true);
                    this.dispose();
                }
                rs.close();
            }
            }catch(SQLException ex){
                System.err.println("sql");
                  JOptionPane.showMessageDialog(this, "Enter correct ID and Name!!");
                
            }
            catch(ClassNotFoundException e){
            System.out.println("class not found");
            }
        
        
        }
    

    }                                        

